mapper.delete:
update sys_user_t
set nt_row_state = 0
where sz_id = #{szId,jdbcType=VARCHAR}

mapper.select:
select sz_id, sz_email, sz_password, sz_phone, ts_created, ts_updated, nt_row_state,
nt_row_version
from sys_user_t
where nt_row_state = 1
offset #{page,jdbcType=INTEGER}
limit #{limit,jdbcType=INTEGER}
